import { Avatar,IconButton } from '@material-ui/core'
import React, { useEffect,useState } from 'react';
import SearchIcon from '@mui/icons-material/Search';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import EmojiEmotionsIcon from '@mui/icons-material/EmojiEmotions';
import MicIcon from '@mui/icons-material/Mic';
import "./CSS/Chat.css";
import { useParams } from 'react-router-dom';
import db from './firebase';


function Chat() {

const {roomId} = useParams();
const [roomName,setRoomName] =useState("");
const [input,setInput] = useState("");
useEffect(()=>{
if(roomId){
    db.collections("rooms").doc(roomId).onSnapshot(snapshot => {
        setRoomName(snapshot.data().name);
    })
}
},[roomId])
const sendMassage=(e)=>{
    e.preventDefault();
    if(input === ""){
        return alert("please enter your message")
    }
    db.collection("rooms").doc(roomId).collection("message").add({
        name:"harsh Singh",
        message:input,
        timestamp:firebase.firestore.FieldValue.serverTimestamp()
    });
    setInput("");
}
    return (
        <div className='chat'>
            <div className="chat__header">
                <Avatar />
                <div className="chat__headerInfo">
                    <h3>{roomName}</h3>
                    <p>Last Seen...</p>
                </div>
                <div className="header__right">
                    <IconButton>
                        <SearchIcon />
                    </IconButton>
                    <IconButton>
                        <AttachFileIcon />
                    </IconButton>
                    <IconButton>
                        <MoreVertIcon />
                    </IconButton>
                </div>
            </div>
            <div className="chat__body">
                <p className='chat__message chat__reciever'>
                    <span className="chat__name">Harsh singh</span>
                    this is new one
                    <span className="chat__time">12:40 PM</span>
                </p>
                <p className='chat__message chat__reciever'>
                    <span className="chat__name">Harsh singh</span>
                    this is new one
                    <span className="chat__time">12:40 PM</span>
                </p>
                <p className='chat__message'>
                    <span className="chat__name">Harsh singh</span>
                    this is new one
                    <span className="chat__time">12:40 PM</span>
                </p>
            </div>


<div className="chat__footer">
    <EmojiEmotionsIcon/>
    <AttachFileIcon/>
    <form onSubmit={sendMassage}>
        <input type="text" value={input} placeholder='Type your message' onChange={
            e=>setInput(e.target.value)
        }/>
        <input type="submit" />
    </form>
    <MicIcon/>
</div>
        </div>
    )
}

export default Chat
